import React, { useState, useEffect } from "react";
import Header from "../Header/header";
import "./dashboard.css";
import Avatar from "@material-ui/core/Avatar";
import axios from "axios";
// import { useDispatch } from "react-redux";

const Dashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const [friends, setFriends] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/user/getFriends", {
        params: {
          userId: "617cc0b8b998f18d6814c439",
        },
      })
      .then((res) => {
        console.log(res);
        setTransactions(res.data);
      })
      .catch((err) => {
        console.log(err);
      });

    axios
      .post("http://localhost:5000/user/addFriend", {
        friendsEmail: "jatinbajaj2001@gmail.com",
        userId: "617ce19ca08f2465fa748cbf",
      })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <>
      <Header />
      <div className="ms-5 mx-3 dash">
        <div className="row">
          <div className="col-lg-2 friends">
            <h4 className="friend mb-4 ms-2">Friends</h4>
            {friends.length === 0 ? (
              <div className="mb-4">
                <h5>No friends Yet!!</h5>
              </div>
            ) : (
              friends.map((friend) => {
                return (
                  <div>
                    <div className="friend-card">
                      <Avatar
                        style={{
                          height: "35px",
                          width: "35px",
                          background: "#1B559C",
                        }}
                      >
                        friend.username.slice(0,1)
                      </Avatar>
                      <h5 className="friend-list">Sarthak Jain</h5>
                    </div>
                  </div>
                );
              })
            )}

            <button type="button" className="btn btn-secondary btn-md">
              Add Friends
            </button>
          </div>
          <div className="col-lg-10 p-0 m-0">
            <div className="row px-5 m-0 d-flex justify-content-between">
              <div className="d-flex justify-content-around col-12 pt-3 pb-2 containerWhite">
                <div className="d-flex flex-column align-items-center">
                  <h3 className="mb-0">
                    <h4 className="d-inline">&#x20B9;</h4> 00.00
                  </h3>
                  Net Balance
                </div>
                <div className="d-flex flex-column align-items-center">
                  <h3 className="mb-0">
                    <h4 className="d-inline">&#x20B9;</h4> 00.00
                  </h3>
                  Credit Amount
                </div>
                <div className="d-flex flex-column align-items-center">
                  <h3 className="mb-0">
                    <h4 className="d-inline">&#x20B9;</h4> 00.00
                  </h3>
                  Debit Amount
                </div>
              </div>
              <div className="d-flex mt-2 mx-0 px-0 justify-content-around">
                <button type="button" className="fs-5 btn btn-primary btn-md">
                  Add expense
                </button>
                <button type="button" className="fs-5 btn btn-primary btn-md">
                  Settle Up
                </button>
              </div>
              <div className="d-flex flex-column align-items-center mt-3 containerWhite py-3 pt-2">
                <h2 className="fw-bold">Transaction History</h2>
                <div className="mt-3">
                  {transactions.length === 0 ? (
                    <h5>No Transactions...</h5>
                  ) : (
                    transactions.map((transactions) => {
                      return <div>Transaction</div>;
                    })
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
